		   	  		and  od.ORDER_SOURCE  in (
					  #{orderSourceIndex}
					)
		   	  
   	 	 )

mapper.select:
  	select  id,pwsysid from t_customers cu   where 1=1
  	
  		and cu.ID in
  		 
			 #{customerid}
		
  	
  	
		and  cu.GRADE_ID    = #{customerGradeID}
	
	
	
		and cu.CITYID in 
		
			 #{cid}
		
	
	
		 and  cu.PWSYSID in (
			select  od.CUSTOMERSID  from t_orders_info od
			where 1=1  
			
				and od.ORDER_SOURCE  in 
				
		            #{os}
		        
			
			
				and  od.PROS_NUM  =  #{pronum}
			
		)

mapper.update:
    	 
			update t_customers set GRADE_ID = #{gradeId}  , GRAGE_UPDATE_LAST = SYSDATE() , GRADE_NAME=#{gradeName}
			where    GRADE_ID != #{gradeId}  
			and  cindex  >= #{ibegin}   and  cindex  <= #{iend}

mapper.select:
    	
	  	select  id ,pwsysid from t_customers cu   where 1=1
	  	
	  	
	  		and cu.ID in 
	  		 
				 #{customerid}
			
	  	
	  	
	  	
			and  cu.GRADE_ID   in 
			 
				 #{id}
			
		
		
	  	
			and  cu.SEX   in 
			 
				 #{id}
			
		
		
	  	
			and  cu.EDUCATION   in 
			 
				 #{id}
			
		
		
		
	  	
			and  cu.CUSTOMERCITY   in 
			 
				 #{id}
			
		
		
		
	  	
	  		 
				 ${baIncomeLevelSearchSql}
			  
		
		
		
		
	  	
			and  cu.MARITALSTATUS   in 
			 
				 #{id}
			
		
		
		
	  	
			and  cu.ISCHILDREN   in 
			 
				 #{id}
			
		
		
		
	  	
	  		
				and  cu.CREATETIME >=   #{baRegTimeStart}
				
		
		
		
	  	
	  		
				and  cu.CREATETIME <=   #{baRegTimeEnd}
			
		
		
		
	  	
			and  cu.CUSTOMERSOURCE   in 
			 
				 #{id}
			
		
		
		
	  	
			and  cu.REGISTEREDSOURCE   in 
			 
				 #{id}
			
		
		
		
		
	  	
			and  cu.SYS_SOURCE  in 
			 
				 #{id}
			
		
		
		
		
			and ( 1=2    
			
				 
		                
		                     or  (cu.CRM_INTEGRAL_USABLE<=300 ) 	
		                
		                 
		                     or  (cu.CRM_INTEGRAL_USABLE >=300 and cu.CRM_INTEGRAL_USABLE <=800  ) 	
		                
		                 
		                     or  (cu.CRM_INTEGRAL_USABLE >=800 and cu.CRM_INTEGRAL_USABLE <=1200  ) 	
		                
		                 
		                     or  (cu.CRM_INTEGRAL_USABLE >=1200 and cu.CRM_INTEGRAL_USABLE <=2000  ) 	
		                
		                 
		                     or  (cu.CRM_INTEGRAL_USABLE >=2000 and cu.CRM_INTEGRAL_USABLE <=3000  ) 	
		                
		                 
		                     or  (cu.CRM_INTEGRAL_USABLE >3000 ) 	
		                
		                
		                 
		         
	        
	        )
		
				
		
		
			and ( 1=2    
			
				 
		                
		                      or  (cu.CINDEX >=300 and cu.CINDEX <=800  )  	
		                
		                 
		                      or  (cu.CINDEX >=100 and cu.CINDEX <=300  )  	
		                
		                 
		                      or  (cu.CINDEX >=300 and cu.CINDEX <=500  )  	
		                
		                 
		                       or  (cu.CINDEX >500 ) 	
		                
		                
		                 
		         
	        
	        )
					
		
		
		
	  	
			  and  cu.GRAGE_UPDATE_LAST >=   #{meUpgradeTimeStart} 	
		
		
		
	  	
			  and  cu.GRAGE_UPDATE_LAST <=   #{meUpgradeTimeEnd} 	
		
		
		
		
			 and  cu.PWSYSID in (
				select  od.CUSTOMERSID  from t_orders_info od
				where 1=1  
				
				
					and od.ORDER_SOURCE  in 
					
			            #{os}
			        
				
				
				
				
					and od.ORDER_STATUS  in 
					
			            #{os}
			        
				
				
				
				
				
					and od.ORDER_STATUS  in 
					
			            #{os}
			        
				
				
				
			  	
					 and  od.PAY_TIME >=   #{paPaymentStartTime} 
				
				
				
			  	
					 	and  od.PAY_TIME <=   #{paPaymentEndTime} 
				
			)
			
			
			
				and  cu.PWSYSID in (
					 select  od.CUSTOMERSID  from t_orders_info od
					where od.PAY_STATUS = "未支付" GROUP BY od.CUSTOMERSID 
					HAVING ( 1=2    
					
						 
				                
				                     or  (SUM(od.ORDER_SUMPRICE)<=300 ) 	
				                
				                 
				                     or  (SUM(od.ORDER_SUMPRICE) >=300 and SUM(od.ORDER_SUMPRICE) <=800  ) 	
				                
				                 
				                     or  (SUM(od.ORDER_SUMPRICE) >=800 and SUM(od.ORDER_SUMPRICE) <=1500  ) 	
				                
				                 
				                     or  (SUM(od.ORDER_SUMPRICE) >=1500 and SUM(od.ORDER_SUMPRICE) <=3000  ) 	
				                
				                 
				                     or  (SUM(od.ORDER_SUMPRICE) >=3000 and SUM(od.ORDER_SUMPRICE) <=5000  ) 	
				                
				                 
				                     or  (SUM(od.ORDER_SUMPRICE) >5000 ) 	
				                
				                
				                 
				         
			        
			        )
					) 
			
			
			
		  	
				and  cu.PWSYSID in (
					 select  od.CUSTOMERSID  from t_orders_info od
					where od.ORDER_STATUS = '已完成' GROUP BY od.CUSTOMERSID 
					HAVING ( 1=2    
					
						 
				                
				                     or  ( COUNT(od.ID) >= 1 and COUNT(od.ID) <= 3 ) 	
				                
				                 
				                     or  ( COUNT(od.ID) >=3 and COUNT(od.ID) <= 5  ) 	
				                
				                 
				                     or  ( COUNT(od.ID) >=5 and COUNT(od.ID) <= 10  ) 	
				                
				                 
				                     or  ( COUNT(od.ID) > 10 ) 	
				                
				                
				                 
				         
			        
			        )
					) 
				
			
			
			
				and  cu.PWSYSID in ( 
					 select  od.CUSTOMERSID  from t_orders_info od WHERE od.PRO_TYPES = #{paCommodityClassb} 
				) 
			
			
			
				and  cu.PWSYSID in ( 
					 select  od.CUSTOMERSID  from t_orders_info od WHERE od.PRO_TYPESA = #{paCommodityClassa} 
				) 
			
			
			
			
				and  cu.PWSYSID in ( select  od.CUSTOMERSID  from t_orders_info od WHERE od.PAY_STATUS = '已支付' 
					
						 and od.PAY_TIME >= #{meLastTradingStart} 
					
					
						 and od.PAY_TIME <= #{meLastTradingEnd} AND PWSYSID NOT IN (
							SELECT
								CUSTOMERSID
							FROM
								t_orders_info
							WHERE
								PAY_STATUS = '已支付'
							AND PAY_TIME > #{meLastTradingEnd} 
						
						)
					
				) 
			
			
	  	
	  	
	  	
		
			
				and  cu.PWSYSID not in (  SELECT CUSTOMERSID FROM t_orders_info WHERE PAY_STATUS = '已支付'   )
			
			
				and  cu.PWSYSID in (  SELECT CUSTOMERSID FROM t_orders_info WHERE PAY_STATUS = '已支付'   )
			
		
	  	
	  	
	  	 	and  cu.PWSYSID in (
	  	 		
	  	 		SELECT
				ad.CUSTOMERSID
				FROM
				T_ADDRESS ad left join t_orders_info od  on ad.ID = od.ADDRESSID 
				where  od.ORDER_STATUS ='已完成' and  ad.cityid IN
				
				 
			            #{id}
			     
			       	 
				group by ad.CUSTOMERSID  
				
				
					having  ( 1=2    
							
								 
						                
						                      or  (count(1) >=1 and count(1) <=3  ) 
						                
						                
						                     or  (count(1) >=3 and count(1) <=5  ) 
						                
						                
						                     or  (count(1) >=5 and count(1) <=10  ) 
						                
					                   
						                     or  (count(1) >10  ) 
						                
						                
						                 
						         
								
					        
				        )
				
	  	 	)
	  	
	  	
	  	
	  			and  cu.PWSYSID in (
		  			select  ewallet.CUSTOMERSID from t_ewallet  ewallet  where ewallet.CREATE_TIME   and  ewallet.PAY_PRICE > 0 AND ewallet.FLAG = 0 
		  			  
		  			 
				  	
						  and  ewallet.CREATE_TIME >=    #{paEwalletRechargeStart} 
					
					
					
				  	
						  and  ewallet.CREATE_TIME <=   #{paEwalletRechargeEnd} 
					
					
					 
				)

where.if:
and cus.id in  (
		select integral.CUSTOMER_ID from t_customer_integral integral where  1=1
		
			  and integral.CREATE_TIME >= #{stime}  
		
		
			  and integral.CREATE_TIME <= #{etime} 
		
	)

mapper.update:
UPDATE T_CUSTOMERS SET CRM_INTEGRAL_SYS=CRM_INTEGRAL_SYS-#{crmIntegralSys} , 
CRM_INTEGRAL_USABLE = CRM_INTEGRAL_SYS-CRM_INTEGRAL_CONSUME ,
LASTTIME = #{lasttime}
where  ID = #{id,jdbcType=BIGINT}

